function ControlFechas(){$('input[name="Crear.Fecha"]').daterangepicker({opens:"center",autoUpdateInput:!1,minDate:moment([objCalendario.anio,0,1,0,0,0,0]),maxDate:moment([objCalendario.anio,11,31,23,59,59,0]),locale:{format:"DD/MM/YYYY",cancelLabel:"Limpiar",applyLabel:"Aplicar",fromLabel:"De",toLabel:"a",daysOfWeek:["Do","Lu","Ma","Mi","Ju","Vi","Sa"],monthNames:["Enero","Febrero","Marzo","Abril","Mayo","Junio","Julio","Agosto","Septiembre","Octubre","Noviembre","Diciembre"]},isInvalidDate:function(n){if(moment(n).weekday()===6||moment(n).weekday()===0)return!0}});$('input[name="Crear.Fecha"]').on("apply.daterangepicker",function(n,t){$(this).val(t.startDate.format("DD/MM/YYYY")+" - "+t.endDate.format("DD/MM/YYYY"))});$('input[name="Crear.Fecha"]').on("cancel.daterangepicker",function(){$(this).val("")})}var objCalendario={anio:0};$(document).ready(function(){objCalendario.anio=parseInt($("#InfoCalendario_Anio").val());ControlFechas()});